import api from './axios-config'; // Importa a configuração centralizada do Axios

export const loginUser = async (username, password) => {
  try {
    const response = await api.post('/login', { username, password });
    const { access_token, user } = response.data;

    // Salva o token no localStorage para uso futuro
    localStorage.setItem('token', access_token);

    return user; // Retorna os dados do usuário
  } catch (error) {
    const message = error.response?.data?.message || 'Erro ao fazer login';
    throw new Error(message);
  }
};

export const registerUser = async (username, password, confirmPassword) => {
  try {
    const response = await api.post('/register', {
      username,
      password,
      confirm_password: confirmPassword,
    });

    return response.data; // Retorna a resposta do registro
  } catch (error) {
    const message = error.response?.data?.message || 'Erro ao registrar';
    throw new Error(message);
  }
};

export const logoutUser = () => {
  // Remove o token do localStorage
  localStorage.removeItem('token');
};
