import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom'; // Importe Navigate
import 'bootstrap/dist/css/bootstrap.min.css'; // CSS do Bootstrap
import Login from './components/Login';
import Register from './components/Register';

// Função para verificar se o usuário está autenticado
const isAuthenticated = () => {
  return localStorage.getItem('token') !== null;
};

function App() {
  return (
    <Router>
      <div className="App">
        <h1>Techcaverna Shop</h1>
        <Routes>
          {/* Rota para a página de Login */}
          <Route
            path="/login"
            element={isAuthenticated() ? <Navigate to="/" /> : <Login />}
          />
          {/* Rota para a página de Registro */}
          <Route
            path="/register"
            element={isAuthenticated() ? <Navigate to="/" /> : <Register />}
          />
          {/* Página principal */}
          <Route
            path="/"
            element={
              isAuthenticated() ? (
                <h2>Bem-vindo à Techcaverna Shop!</h2>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
