import React, { useState } from 'react';
import api from '../api/axios-config';

const AddProduct = () => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [stock, setStock] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault(); // Evita o recarregamento da página no submit

        try {
            // Fazendo o POST para o backend
            const response = await api.post('/api/add-product', {
                name,
                description,
                price: parseFloat(price), // Converte o preço para número
                stock: parseInt(stock), // Converte o estoque para número
            });

            // Mostra mensagem de sucesso
            setMessage(response.data.message);

            // Reseta os campos após adicionar o produto
            setName('');
            setDescription('');
            setPrice('');
            setStock('');
        } catch (error) {
            // Tratamento de erro
            if (error.response) {
                // Erro vindo do backend
                setMessage(error.response.data.message || 'Erro no backend.');
                console.error('Erro do backend:', error.response.data);
            } else if (error.request) {
                // Nenhuma resposta do servidor
                setMessage('Nenhuma resposta do servidor.');
                console.error('Erro de requisição:', error.request);
            } else {
                // Outro tipo de erro
                setMessage('Erro inesperado.');
                console.error('Erro:', error.message);
            }
        }
    };

    return (
        <div>
            <h2>Adicionar Produto</h2>
            {/* Exibe a mensagem de sucesso ou erro */}
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Nome"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Descrição"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Preço"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Estoque"
                    value={stock}
                    onChange={(e) => setStock(e.target.value)}
                    required
                />
                <button type="submit">Adicionar</button>
            </form>
        </div>
    );
};

export default AddProduct;
