// src/components/Register.js
import React, { useState } from 'react';
import { registerUser } from '../api/auth';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    const result = await registerUser(username, password, confirmPassword);
    if (result.message === "Usuário registrado com sucesso") {
      console.log("Registro feito com sucesso!");
    } else {
      console.error("Erro:", result.message);
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Usuário" />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Senha" />
      <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} placeholder="Confirme a Senha" />
      <button type="submit">Registrar</button>
    </form>
  );
};

export default Register;
