import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Importe os módulos necessários
import 'bootstrap/dist/css/bootstrap.min.css'; // CSS do Bootstrap
import Login from './components/Login'; // Componentes
import Register from './components/Register';

function App() {
  return (
    <Router>
      <div className="App">
        <h1>Techcaverna Shop</h1>
        <Routes>
          {/* Rota para a página de Login */}
          <Route path="/login" element={<Login />} />
          {/* Rota para a página de Registro */}
          <Route path="/register" element={<Register />} />
          {/* Página principal */}
          <Route path="/" element={<h2>Bem-vindo à Techcaverna Shop!</h2>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
