import React, { useState } from 'react';
import Login from './components/Login';
import Customers from './components/Customers';  // Se j치 existir
import Products from './components/Products';    // Se j치 existir
import './App.css';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));

  return (
    <div className="App">
      <h1>TechCaverna</h1>
      {!token ? (
        <Login setToken={setToken} />
      ) : (
        <div>
          <Customers token={token} />
          <Products token={token} />
          <button onClick={() => {
            setToken(null);
            localStorage.removeItem('token');
          }}>Logout</button>
        </div>
      )}
    </div>
  );
}

export default App;
